# https://leetcode.com/problems/permutation-in-string/description/

from test import test


class Solution:
    def checkInclusion(self, str1: str, str2: str) -> bool:
        str1_map = self.create_map(str1)
        total = len(str1)

        right = 0
        left = 0

        current_total = 0

        while right < len(str2):
            right_char = str2[right]

            if right_char not in str1_map:
                right += 1

                while left < right:
                    left_char = str2[left]

                    if left_char in str1_map:
                        str1_map[left_char] += 1

                    left += 1

                current_total = 0
                continue

            str1_map[right_char] -= 1
            current_total += 1

            if str1_map[right_char] < 0:
                while str1_map[right_char] < 0 and left < right:
                    left_char = str2[left]

                    str1_map[left_char] += 1
                    current_total -= 1

                    left += 1

            if current_total == total:
                return True

            right += 1

        return False

    def create_map(self, string):
        str_map = {}

        for char in string:
            str_map[char] = str_map[char] + 1 if char in str_map else 1

        return str_map


solution = Solution()

